import React, { useState, useEffect } from "react";
import img_knot from "../../Images/knot.png";
import video from "../../Videos/YuriyAndFrogs.mp4";
import Slider from '../Slider/Slider'
import "./Container.css";

function Container(props) {
  const sliderArrange = ["<p>QQ</p>", "<p>SS</p>"];
  const [slider, setSlider] = useState(sliderArrange[0]);
  useEffect(() => {
    let interval = setInterval(() => {
      if (sliderArrange.indexOf(slider) != sliderArrange.length - 1) {
        setSlider(sliderArrange[sliderArrange.indexOf(slider) + 1]);
      } else setSlider(sliderArrange[0]);
    }, 1000);

    return () => clearInterval(interval);
  });

  return (
    <section className="wbox_content">
      <div className="wbox_wrapper">
        <div className="wbox_top">
          <div className="heart"></div>
        </div>
        <article className="wbox_middle">
          <h2>{props.hText[0]}</h2>
          <Slider />
        </article>
        <article className="wbox_footer">
          <h2>{props.hText[1]}</h2>
          <video src={video} controls="controls"></video>
        </article>
      </div>
      <header>
        <span className="wbox_images">
          <img src={img_knot} alt="knot" className="image_knot left_knot"></img>
          <img src={img_knot} alt="knot" className="image_knot"></img>
        </span>
      </header>
    </section>
  );
}

export default Container;
